import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props){
    super(props);
        this.state={
            selectedLevel: "Level first",
            toShowMenu: false,
            songsDetails:[]
        };
        this.fetchSongs = this.fetchSongs.bind(this);
        this.fetchSongs();
    }

    fetchSongs(){
      var sRequestURL="http://127.0.0.1:5000/";
      // ajax request to get the song details
      $.ajax({
        url: sRequestURL,
        type: "GET",
        async: false,
        contentType: "application/json; charset=utf-8",
        success: function(data){if(data){
            this.setState({songsDetails:JSON.parse(data)});
            console.log(this.state.songsDetails);
        }
    },
        failure: function (response) {
            alert(response.responseText);
        },
        error: function (response,status) {
            alert(status);
        }
    });
    }

  render() {
    return (
      <div >
        <div className="App">Welcome to Songs Collection</div>
        <div className="headContainer">
        <input type="text" className="App-Search" placeholder="Search song"/>
        <div className="fa fa-search search-icon"></div>
        </div>
        <div className="songContainer">Hello world</div>
      </div>
    );
  }
}

export default App;
