import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props){
    super(props);
        this.state={
            selectedLevel: "Level first",
            toShowMenu: false,
            songsDetails:[]
        };
        this.fetchSongs = this.fetchSongs.bind(this);
        this.fetchSongs();
    }

    fetchSongs(){
      fetch("http://127.0.0.1:5000/")
      .then(res => res.json())
      .then(
        (result) => {
         this.setState({songsDetails:JSON.parse(result).status});
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
    }

  render() {
    const items = this.state.songsDetails.map(function(item){
      return <li> {item} </li>;
    });
    return (
      <div >
        <div className="App">Welcome to Songs Collection</div>
        <div className="headContainer">
        <input type="text" className="App-Search" placeholder="Search song"/>
        <div className="fa fa-search search-icon"></div>
        </div>
        <div className="songContainer">{items}</div>
      </div>
    );
  }
}

export default App;
