import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props){
    super(props);
        this.state={
            selectedLevel: "Level first",
            toShowMenu: false,
            songsDetails:[]
        };
        this.fetchSongs = this.fetchSongs.bind(this);
        this.fetchSongs();
    }

    fetchSongs(){
      fetch("http://127.0.0.1:5000/")
      .then(res => res.json())
      .then(
        (result) => {
         this.setState({songsDetails:JSON.parse(result).status});
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
    }

  render() {

    let generateTable=[];
    generateTable.push('<tr>');
    for(let iCounter=0;iCounter<Object.keys(this.state.songsDetails).length;iCounter++){
      generateTable.push('<td><div>'+this.state.songsDetails[iCounter][0]+'</div></td>');
      if(iCounter%5 === 0 && iCounter < this.state.songsDetails.length-1){
        generateTable.push('</tr><tr>');
      }
      else if(iCounter === this.state.songsDetails.length-1){
        generateTable.push('</tr>');
      }
    }
    return (
      <div >
        <div className="App">Welcome to Songs Collection</div>
        <div className="headContainer">
        <input type="text" className="App-Search" placeholder="Search song"/>
        <div className="fa fa-search search-icon"></div>
        </div>
        <div className="songContainer">
          <table>
            <tr><th></th><th></th><th></th><th></th><th></th></tr>
            <tr>
              {Object.keys(this.state.songsDetails).map(function(row){
                  return<td>{this.state.songsDetails[row]}</td>
              })}
            </tr>
          </table>
        </div>
      </div>
    );
  }
}

export default App;
